import React from 'react';
import Auxi from '../../../hoc/Auxi';

import './InputField.css';

class InputField{


   shouldComponentUpdate(nextProps, nextState){
      return props.errors !== nextProps.errors;
   }


   render() {
      return (
         <Auxi>
            <input
               className="Input"
               type={props.type}
               name={props.name}
               value={props.value}
               onChange={props.changed}
               placeholder={props.placeholder}
            />
            {console.log(props.errors)}

            {props.errors[FieldName] ? (<div className="errorFeedback">{props.errors[FieldName]}</div>) : null}
         </Auxi>

      );
   }
};

export default InputField;
