import React, { Component } from 'react';
import './App.css';
import Splash from './components/ui/Splash';

class App extends Component {
  render() {

     let hideSplash = true;

     splashDelay = () => {
        setTimeout(() => {
           hideSplash = false;
        }, 3);
     };

     return (
        <div className="App">
           <Splash hideSplash = {hideSplash}/>
        </div>
     );
  }
}

export default App;
