import React from 'react';
import './NavHotels.css';
import '../AuthNavigationItem.css';
import { NavLink } from 'react-router-dom';

import hotelIcon from '../../../../../img/navImg/hotels.svg';

const NavHotels = (props) => {
   let DropHotels = null;
   const onClickHandler = (e) => {
      DropHotels  = (
         <div className="DropHotels">
            <NavLink className = "DropHotelsLink" to="/hotels/recommended">Recommended Hotels</NavLink>
            <NavLink className = "DropHotelsLink" to="/hotels/rated">Top Rated Hotels</NavLink>
            <NavLink className = "DropHotelsLink" to="/hotels/newest">Top New Hotels</NavLink>
         </div>
      );
   };
   return (
      <li className ="AuthNavigationItem" onClick = {onClickHandler}>
            <img className="AuthNavIcon" src={hotelIcon} alt="Hotel Icon of CrowApp"/>
             {DropHotels}
      </li>
   )
};



const Landing = (props) => {
   const match = matchMedia("(min-width: 450px)");

   const backImg = match.matches?desktopbg:androidbg;

   const onSignupHandler = (e) => {
      e.preventDefault();
      props.history.push('/signup');
   };

   const onLoginHandler = (e) => {
      e.preventDefault();
      props.history.push('/login');
   };

   return(
      <div className="Landing">
         <img src={backImg} alt="Background of CrowApp"/>
         <div className="Container">
            <Button cls = "Success" clicked = {onSignupHandler} >Sign Up</Button>
            <Button cls = "Success" clicked = {onLoginHandler} >Log In</Button>
            <div className="Command">
               Log In to find top hotels
            </div>

         </div>
      </div>
   )
};

export default NavHotels;
