import React, {Component} from 'react';
import {connect} from 'react-redux';

import InputField from '../../../../components/ui/InputField/InputField';
import Button from '../../../../components/ui/Button/Button';
import './EditProfile.css';

import {editCurrentProfile} from '../../../../redux/actions/profileActions';

class EditProfile extends Component {
   constructor(){
      super();
      this.state = {
         username: '',
         location: '',
         bio: '',
         facebook: '',
         twitter: '',
         linkedin: '',
         instagram: '',
         errors : {}
      }
   }


   onChangeHandler = (e) => {
      e.preventDefault();
      this.setState({[e.target.name]: e.target.value});
   };

   onSaveHandler = (e) => {
      e.preventDefault();
      const profileData = {

      }
      this.props.editCurrentProfile();
   };


   render() {
      return (
         <div className="EditProfileBox">
            <h1>Edit Profile</h1>
            <InputField
               type = "text"
               name = 'username'
               placeholder = 'Username'
               changed = {this.onChangeHandler}
               value = {this.state.username}
               errors={this.state.errors}
            />
            <InputField
               type = "text"
               name = 'location'
               placeholder = 'Location'
               changed = {this.onChangeHandler}
               value = {this.state.location}
               errors={this.state.errors}
            />
            <InputField
               type = "text"
               extraCls = "TextArea"
               name = 'bio'
               placeholder = 'Bio'
               changed = {this.onChangeHandler}
               value = {this.state.bio}
               errors={this.state.errors}
            />

            <InputField
               type = "url"
               extraCls = "SocialMediaLink"
               name = 'facebook'
               placeholder = 'Facebook Link'
               changed = {this.onChangeHandler}
               value = {this.state.facebook}
               errors={this.state.errors}
            />

            <InputField
               type = "url"
               extraCls = "SocialMediaLink"
               name = 'twitter'
               placeholder = 'Twitter Link'
               changed = {this.onChangeHandler}
               value = {this.state.twitter}
               errors={this.state.errors}
            />

            <InputField
               type = "url"
               extraCls = "SocialMediaLink"
               name = 'linkedin'
               placeholder = 'LinkedIn Link'
               changed = {this.onChangeHandler}
               value = {this.state.linkedin}
               errors={this.state.errors}
            />

            <InputField
               type = "url"
               extraCls = "SocialMediaLink"
               name = 'instagram'
               placeholder = 'Instagram Link'
               changed = {this.onChangeHandler}
               value = {this.state.instagram}
               errors={this.state.errors}
            />

            <Button clicked={this.props.modalClosed} cls = "Warning InlineBtn" >Cancel</Button>
            <Button clicked={this.onSaveHandler} cls = "Success InlineBtn" >Save</Button>
         </div>

      );
   }
}

function mapStateToProps(state) {
   return {
      profile: state.profile,
      errors: state.errors
   };
}

EditProfile.propTypes = {
   editCurrentProfile: PropTypes.func.isRequired,
   profile: PropTypes.object.isRequired,
   errors: PropTypes.object.isRequired
};

export default connect(mapStateToProps,{editCurrentProfile})(EditProfile);
