import React, {Component} from 'react';

import Button from '../../components/ui/button/Button';
import userIcon from '../../img/navImg/user-plus-solid.svg';
import './SignUp.css';

class SignUp extends Component{
      state = {
         userData: {
            name:'',
            email:'',
            password: '',
            password2: '',
            faculty: '',
            errors: {}
         }

      };

   render(){
      return (
         <div className= "FormBox" id = "signUpForm">
               <img className="UserIcon" src={userIcon} alt=""/>
            <form className="Form" noValidate>
               <input className="Input" type="text" placeholder = "Full Name"/>
               <input className="Input" type="email" placeholder = "Email"/>
               <input className="Input" type="password" placeholder = "Password"/>
               <input className="Input" type="password" placeholder = "Confirm Password"/>
            </form>
            <select name="faculty" className="Input" form="signUpForm">
               <option value="">Faculty</option>
               <option value="Computer">Computer Engineering</option>
               <option value="Civil">Civil Engineering</option>
               <option value="Mechanical">Mechanical Engineering</option>
               <option value="Electronics">Electronics Engineering</option>
               <option value="Electrical">Electrical Engineering</option>
               <option value="Geomatics">Geomatics Engineering</option>
               <option value="Automobile">Automobile Engineering</option>
            </select>

              <Button cls = "Success" value = "Sign Up" />

         </div>
      )
   }
}

export default SignUp;
