import React from 'react';
import splashdPhoto from '../../img/splashd.png';
import splashaPhoto from '../../img/splasha.png';
import './Splash.css';

const splash = () => {

   let x = matchMedia("(max-width: 700px)");

   const splashPhoto = x.matches ? splashaPhoto : splashdPhoto;

   let splashScreen = null;
   const splashDelay = () => {
      setTimeout(() => {
         splashScreen = (<div className='Splash'> <img src={splashPhoto} alt=''/>
            <div className='loading'>
               <div className='loading-bar'> </div>
               <div className='loading-bar'> </div>
               <div className='loading-bar'> </div>
               <div className='loading-bar'> </div>
            </div>
         </div>);
      }, 3000);
   };


   return  <div onLoad={splashDelay()}>{splashScreen}</div>;

};

export default splash;
