import React, { Component } from 'react';
import './NavHotels.css';
import '../AuthNavigationItem.css';
import { NavLink } from 'react-router-dom';

import hotelIcon from '../../../../../img/navImg/hotels.svg';

class NavHotels extends Component{
   render() {
      let DropHotels = null;
      onClickHandler = (e) => {
         DropHotels = (
            <div className="DropHotels">
               <NavLink className="DropHotelsLink" to="/hotels/recommended">Recommended Hotels</NavLink>
               <NavLink className="DropHotelsLink" to="/hotels/rated">Top Rated Hotels</NavLink>
               <NavLink className="DropHotelsLink" to="/hotels/newest">Top New Hotels</NavLink>
            </div>
         );
      };
      return (
         <li className="AuthNavigationItem" onClick={this.onClickHandler}>
            <img className="AuthNavIcon" src={hotelIcon} alt="Hotel Icon of CrowApp"/>
            {DropHotels}
         </li>
      )
   }
};

export default NavHotels;
