import React from 'react';
import splashdPhoto from '../../img/splashd.png';
import splashaPhoto from '../../img/splasha.png';
import './Splash.css';

const splash = () => {
   let x = matchMedia("(max-width: 700px)");
   const splash = x.matches?splashaPhoto:splashdPhoto;

      

   return (
         <div className="Splash">
            <img src={splash} alt=""/>
            <div className="loading">
               <div className="loading-bar"> </div>
               <div className="loading-bar"> </div>
               <div className="loading-bar"> </div>
               <div className="loading-bar"> </div>
            </div>
         </div>
      )

};

export default splash;
