import React, {Component} from 'react';
import axios from 'axios';

import Button from '../../components/ui/button/Button';
import userIcon from '../../img/navImg/user-plus-solid.svg';
import './SignUp.css';

class SignUp extends Component{
   constructor(){
      super();
      this.state = {
         name:'',
         email:'',
         password: '',
         password2: '',
         faculty: '',
         errors: {}
      }
   };

   onChangeHandler = (e) => {
      this.setState({[e.target.name] : e.target.value})
   };

   onClickHandler = (e) => {
      e.preventDefault(); //prevents from default submission
      const newUser =  {
         name: this.state.name,
         email: this.state.email,
         password: this.state.password,
         password2: this.state.password2,
         faculty: this.state.faculty,
      };
      axios.post('api/users/register', newUser)
         .then(res => console.log(res.data))
         .catch(err => console.log(err.response.data));
   };


   render(){
      return (
         <div className= "FormBox" id = "signUpForm">
            <img className="UserIcon" src={userIcon} alt="Sign Up Icon for CrowApp"/>
            <form className="Form" noValidate>

               <input
                  className="Input"
                  value = {this.state.name}
                  type="text"
                  placeholder = "Full Name"
                  name = "name"
                  onChange={this.onChangeHandler}
               />
               <input
                  className="Input"
                  value = {this.state.email}
                  type="email"
                  placeholder = "Email"
                  name = "email"
                  onChange={this.onChangeHandler}
               />
               <div></div>
               <input
                  className="Input"
                  value = {this.state.password}
                  type="password"
                  placeholder = "Password"
                  name = "password"
                  onChange={this.onChangeHandler}
               />
               <input
                  className="Input"
                  value = {this.state.password2}
                  type="password"
                  placeholder = "Confirm Password"
                  name = "password2"
                  onChange={this.onChangeHandler}
               />
            </form>

            <select
               className="Input"
               value = {this.state.faculty}
               form="signUpForm"
               name="faculty"
               onChange={this.onChangeHandler}
            >


               <option value="">
                  Faculty
               </option>
               <option value="Computer">
                  Computer Engineering
               </option>
               <option value="Civil">
                  Civil Engineering
               </option>
               <option value="Mechanical">
                  Mechanical Engineering
               </option>
               <option value="Electronics">
                  Electronics Engineering
               </option>
               <option value="Electrical">
                  Electrical Engineering
               </option>
               <option value="Geomatics">
                  Geomatics Engineering
               </option>
               <option value="Automobile">
                  Automobile Engineering
               </option>


            </select>

            <Button cls = "Success" value = "Sign Up" clicked = {this.onClickHandler} />

         </div>
      )
   }
}

export default SignUp;
