import React from 'react';
import Auxi from '../../../hoc/Auxi';

import './InputField.css';

const InputField = (props) => {
   return (
      <Auxi>
         <input
            className="Input"
            type={props.type}
            name={props.name}
            value = {props.value}
            onChange={props.changed}
            placeholder={props.placeholder}
         />
         {props.name}

         {props.errors[props.name]? (<div className="errorFeedback">{props.errors[props.name]}</div>):null}
      </Auxi>

   );
};

export default InputField;
