import React, { Component } from 'react';
import './App.css'; //CSS for all of the apps
import Splash from './components/ui/Splash';
import SignUp from './containers/SignUp/SignUp';
import Login from './containers/Login/Login';
import {BrowserRouter as Router, Route} from 'react-router-dom';
import NavigationItems from './components/Navigation/NavigationItems/NavigationItems';
import Landing from './components/Landing/Landing';
import AllHotels from './components/AllHotels/AllHotels';
import Footer from './components/Footer/Footer';


class App extends Component {
   state = {
      hideSplash: false
   };

   componentDidMount(){
      setTimeout(() => {
         this.setState({hideSplash : true});
      }, 10);
   }


   render() {


      return (
         <Router>
            <div className="App">
               <Splash hideSplash = {this.state.hideSplash}/>
               <NavigationItems/> /*These are for the navigation items*/
               <Route exact path = "/" component = {Landing} />
               <Route exact path = "/signup" component = {SignUp} />
               <Route exact path = "/login" component = {Login} />
               <Route exact path = "/allHotels" component = {AllHotels} />

               <Footer/>
            </div>
         </Router>
      );
   }
}

export default App;
