import React, {Component} from 'react';
import {connect} from 'react-redux';
import PropTypes from 'prop-types';

import InputField from '../../../../components/ui/InputField/InputField';
import Button from '../../../../components/ui/Button/Button';
import './EditProfile.css';

import {getCurrentProfile,editCurrentProfile} from '../../../../redux/actions/profileActions';

class EditProfile extends Component {
   constructor(){
      super();
      this.state = {
         handle: ' ',
         location: ' ',
         bio: ' ',
         facebook: ' ',
         twitter: ' ',
         linkedIn: ' ',
         instagram: ' ',
         errors : {}
      }
   }




   onChangeHandler = (e) => {
      e.preventDefault();
      this.setState({[e.target.name]: e.target.value});
   };

   onSaveHandler = (e) => {
      e.preventDefault();
      const profileData = {
         handle: this.state.handle,
         location: this.state.locaton,
         bio: this.state.bio,
         facebook: this.state.facebook,
         twitter: this.state.twitter,
         linkedIn: this.state.linkedIn,
         instagram: this.state.instagram
      };
      this.props.editCurrentProfile(profileData);
   };


   componentWillReceiveProps(nextProps) {
      if(nextProps.errors) {
         this.setState({errors: nextProps.errors});
      }

      const profile = nextProps.profile.profile;


      if(profile) {
         this.setState({handle: profile.handle});
      }


      if(this.props.profile.profile !== nextProps.profile.profile){
         // console.log('notEqual');
         // console.log(this.props.profile.profile);
         // console.log(nextProps.profile.profile);
      }
   }




   render() {
      return (
         <form className="EditProfileBox" noValidate>
            <h1>Edit Profile</h1>
            <InputField
               type = "text"
               name = "handle"
               placeholder = "Username"
               changed = {this.onChangeHandler}
               value = {this.state.handle}
               errors={this.state.errors}
            />
            <InputField
               type = "text"
               name = "location"
               placeholder = "Location"
               changed = {this.onChangeHandler}
               value = {this.state.location}
               errors={this.state.errors}
            />
            <InputField
               extraCls = "TextArea"
               type = "text"
               name = "bio"
               placeholder = "Bio"
               changed = {this.onChangeHandler}
               value = {this.state.bio}
               errors={this.state.errors}
            />

            <InputField
               type = "url"
               extraCls = "SocialMediaLink"
               name = "facebook"
               placeholder = "Facebook Link"
               changed = {this.onChangeHandler}
               value = {this.state.facebook}
               errors={this.state.errors}
            />

            <InputField
               type = "url"
               extraCls = "SocialMediaLink"
               name = "twitter"
               placeholder = "Twitter Link"
               changed = {this.onChangeHandler}
               value = {this.state.twitter}
               errors={this.state.errors}
            />

            <InputField
               type = "url"
               extraCls = "SocialMediaLink"
               name = "linkedIn"
               placeholder = "LinkedIn Link"
               changed = {this.onChangeHandler}
               value = {this.state.linkedIn}
               errors={this.state.errors}
            />

            <InputField
               type = "url"
               extraCls = "SocialMediaLink"
               name = "instagram"
               placeholder = "Instagram Link"
               changed = {this.onChangeHandler(name)}
               value = {this.state.instagram}
               errors={this.state.errors}
            />

            <Button clicked={this.props.modalClosed} cls = "Warning InlineBtn" >Cancel</Button>
            <Button clicked={this.onSaveHandler} cls = "Success InlineBtn" >Save</Button>
         </form>

      );
   }
}

function mapStateToProps(state) {
   return {
      profile: state.profile,
      errors: state.errors
   };
}

EditProfile.propTypes = {
   getCurrentProfile: PropTypes.func.isRequired,
   editCurrentProfile: PropTypes.func.isRequired,
   profile: PropTypes.object.isRequired,
   errors: PropTypes.object.isRequired
};

export default connect(mapStateToProps,{getCurrentProfile, editCurrentProfile})(EditProfile);
